// material design btn
import Ripples from "react-ripples";

const Btn = ({ children, className, onClick }) => {
  return (
    <Ripples
      className={`cursor-pointer ${className} overflow-hidden rounded-md`}
      onClick={onClick}
      duration={400}
    >
      {children}
    </Ripples>
  );
};

export default Btn;
